package javaInfoTeck;

public class SwitchCases_0002 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
/**
 * Switch Case 
 */
		
		int myInt0001 = 8;
		String myString0001 ;
		
		switch (myInt0001) {
		case 1: myString0001 = "January";
		break;
		case 2: myString0001 = "February";
		break;
		case 3: myString0001 = "March";
		break;
		case 4: myString0001 = "April";
		break;
		case 5: myString0001 = "May";
		break;
		case 6: myString0001 = "June";
		break;
		case 7: myString0001 = "July";
		break;
		case 8: myString0001 = "August";
		break;
		case 9: myString0001 = "September";
		break;
		case 10: myString0001 = "October";
		break;
		case 11: myString0001 = "November";
		break;
		case 12: myString0001 = "December";
		break;
		default: myString0001 = "Invalid month";
		break;
		}
		System.out.println(myString0001);
	}

}
